const toggleSearch = () => {
  selElem('.navbar-nav.navbar-nav--header').classList.toggle('d-xl-none'),
    selElem('.form.form--search').classList.toggle('should-show'),
    selElem('#header').classList.toggle('pb-5');
};
window.addEventListener('scroll', () => {
  const s = selElem('#header'),
    { offsetHeight: e } = s,
    { pageYOffset: a } = window,
    l = a < e;
  if (!l) {
    const { pathname: e } = window.location;
    return (
      s.classList.add('shadow'),
      e.includes('index') || !e.includes('.html')
        ? s.classList.add('bg-success--custom')
        : s.classList.add('bg-white')
    );
  }
  s.classList.remove('shadow'),
    s.classList.remove('bg-white'),
    s.classList.remove('bg-success--custom');
});
